package basicclass Colatz {    fun solution(num: Int): Int {        if (num == 1) return 0        var copyNum = num        var count = 0        var flag = true        for (i in 1..500) {            flag = checkFlag(copyNum)            when (flag) {                true -> copyNum /= 2                false -> {                    copyNum *= 3                    copyNum += 1                }            }            count = i            if (copyNum == 1) return count            if (count == 500) return -1        }        return -1    }    fun checkFlag(num: Int): Boolean {        if (num % 2 == 0) return true        if (num % 2 == 1) return false        return true    }}