package basicimport java.util.*class NumDesc {    fun solution(n: Long): Long {        val nStr = n.toString()        var answer = StringBuilder()        val cArr = nStr.toCharArray()        val list = cArr.sortedByDescending { it }        for (e in list) {            answer.append(e)        }        println(answer.toString())        return answer.toString()            .toLong()    }}fun main() {    println(NumDesc().solution(1132))}