package basicclass SumOfBetweenTwoNums {    fun solution(a: Int, b: Int): Long {        var answer: Long = 0        var flag = a <= b        when (flag) {            true -> {                for (i in a..b) {                    answer += i                }            }            false -> {                for (i in b..a) {                    answer += i                }            }        }        return answer    }}