package lv2class GcdAndLcm {    fun solution(n: Int, m: Int): IntArray {        var answer = mutableListOf<Int>()        answer += GCD(n, m)        answer += LCM(n, m)        return answer.toIntArray()    }    fun GCD(a: Int, b: Int): Int {        var tA = a        var tB = b        while (tB != 0) {            val r = tA % tB            tA = tB            tB = r        }        return tA    }    fun LCM(a: Int, b: Int) = a * b / GCD(a, b)}