package lv2class RowColSum {    fun solution(arr1: Array<IntArray>, arr2: Array<IntArray>): Array<IntArray> {        val answer = mutableListOf<IntArray>()        for (i in arr1.indices) {            val sub = mutableListOf<Int>()            for (j in arr1[i].indices) {                sub += arr1[i][j] + arr2[i][j]            }            answer += intArrayOf(*sub.toIntArray())        }        return answer.toTypedArray()    }}