package lv3class StrangeStringMaker {    fun solution(s: String): String {        var answer = ""        var idx = 0        s.map {            if (it.toString().isBlank()) {                answer += it                idx = 0            } else {                if (idx % 2 == 0) {                    answer += it.uppercase()                } else {                    answer += it.lowercase()                }                idx++            }        }        return answer    }}